use bytes::Bytes;
use rlp::{UntrustedRlp, RlpStream, Decodable, Encodable, DecoderError};
use header::Header;
use transaction::Transaction;

#[derive(Default, Debug, Clone, PartialEq, Eq)]
pub struct Block {
    pub header: Header,
    pub transactions: Vec<Transaction>,
}

impl Block {
    pub fn rlp_bytes(&self, seal: bool) -> Bytes {
        let mut buf = RlpStream::new_list(3);
        self.header.stream_rlp(&mut buf, seal);
        buf.append_list(&self.transactions);
        buf.out()
    }
}

impl Encodable for Block {
    fn rlp_append(&self, s: &mut RlpStream) {
        s.begin_list(2);
        s.append(&self.header);
        s.append_list(&self.transactions);
    }
}


impl Decodable for Block {
    fn decode(rlp: &UntrustedRlp) -> Result<Self, DecoderError> {
        if rlp.item_count()? != 2 {
            return Err(DecoderError::RlpIncorrectListLen);
        }
        Ok(Block {
            header: rlp.val_at(0)?,
            transactions: rlp.list_at(1)?,
        })
    }
}

#[cfg(test)]
mod tests {
    use rustc_hex::FromHex;
    use rlp;
    use super::Block;

    #[test]
    fn test_block_decode() {
        let block_rlp = "".from_hex().unwrap();
        let block: Block = rlp::decode(&block_rlp);
        assert_eq!(block.transactions.len(), 1);
    }
}
